commits.content:
* {
  margin-left: 0px;
}

nav {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 96vw;
  height: 5vh;
  padding: 0 2%;
  top: 0;
  background-color: #FFE353;
}

select {
  background: inherit;
  border: none;
}

a {
  text-decoration: none;
  color: black;
}


i {
  color: black;
  margin: 0 5px 0 10px;

}

.container {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 98vw;
  height: 38vh;
  margin-top: 3vh;
}

.welcome {
  background-color: #eeee;
  padding: 3%;
  width: 90vw;
  height: 30vh;
}

.welcome>p {
  font-size: smaller;
}

.welcome>input,
.welcome>button {
  display: inline-block;
  width: 80%;
  border-radius: 5px;
  margin: 1%;
  border: 5px solid white
}

.welcome>button {
  width: 70px;
  background-color: #f5f542;
  border-color: #f5f542;
  height: 40px;
}

button:hover {
  transform: scale(1.1);
  transition: all 1s;

}

.loading {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 2%;
  width: 90vw;
  height: 90vh;
}

.loadspan {
  border-left: 5px solid black;
  border-radius: 50%;
  width: 50px;
  height: 50px;
  animation-name: load;
  animation-duration: 0.5s;
  animation-iteration-count: infinite
}

@keyframes load {
  25% {
    transform: rotate(90deg);
    border-color: red;
  }

  50% {
    transform: rotate(180deg);
    border-color: blue;
  }

  75% {
    transform: rotate(250deg);
    border-color: green;
  }

  100% {
    transform: rotate(360deg);


  }
}

.movies {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
}

.pagination {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 50vw;
  overflow: hidden;
  margin: auto;
}

.paginationBtn {
  background-color: inherit;
  color: cadetblue;
  border: none;
}

.paginationBtn:focus {
  background-color: #f5f542;
  border-color: #f5f542 !important;
}
